import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";

export default function QuizResultado() {
  const { user_id } = useParams(); // üëà precisa combinar com o nome da rota
  const [resultado, setResultado] = useState(null);
  const [erro, setErro] = useState(null);

  useEffect(() => {
    const fetchResultado = async () => {
      try {
        const res = await fetch(`http://localhost:3000/quizresultado/${user_id}`);
        if (!res.ok) throw new Error(`Erro ao buscar resultado: ${res.status}`);
        const data = await res.json();
        setResultado(data);
      } catch (err) {
        console.error(err);
        setErro(err.message);
      }
    };

    if (user_id) fetchResultado();
  }, [user_id]);

  if (erro)
    return (
      <div style={{ color: "red", padding: "20px" }}>
        <p>‚ùå {erro}</p>
        <p>Verifique se o backend est√° rodando e o user_id est√° correto.</p>
      </div>
    );

  if (!resultado)
    return (
      <div
        style={{
          backgroundColor: "#000",
          color: "#fff",
          height: "100vh",
          display: "flex",
          justifyContent: "center",
          alignItems: "center",
        }}
      >
        <p>Carregando resultado...</p>
      </div>
    );

  // campos vindos do banco
  const { score, result_title, result_text } = resultado;

  return (
    <div
      style={{
        backgroundColor: "#000",
        color: "#fff",
        minHeight: "100vh",
        display: "flex",
        justifyContent: "center",
        alignItems: "center",
      }}
    >
      <div
        style={{
          border: "1px solid #007BFF",
          padding: "40px",
          borderRadius: "10px",
          textAlign: "center",
          width: "50%",
        }}
      >
        <h2 style={{ color: "#fff", marginBottom: "10px" }}>{result_title}</h2>
        <p style={{ fontSize: "16px", marginBottom: "15px" }}>{result_text}</p>
        <p>Pontua√ß√£o total: {score}</p>
      </div>
    </div>
  );
}
