  appId: "1:952537486987:web:accc6af0f05e3ac413e68e",
  measurementId: "G-QP2N20XTXD"
};

// Inicializa Firebase
const app = initializeApp(firebaseConfig);
const analytics = getAnalytics(app);

// Inicializa Auth, Firestore e Functions
export const auth = getAuth(app);
export const db = getFirestore(app);
// >>> NOVO: Inicializa o serviço de Cloud Functions
const functions = getFunctions(app);


/**
 * Função auxiliar para chamar a Cloud Function 'sendEmail'.
 * @param {object} data - Deve conter { to: string, subject: string, html: string }
 * @returns {Promise} Retorna a resposta da função (com 'success: true' ou erro).
 */
export const callSendEmailFunction = (data) => {
    // Cria a referência para a Cloud Function 'sendEmail'
    const sendEmail = httpsCallable(functions, 'sendEmail');
    
    // Chama a função com os dados (to, subject, html)
    return sendEmail(data);
};

