// src/components/Navbar.js

import React, { useState, useEffect, useRef } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { auth } from '../services/firebaseService'; 
import { signOut } from 'firebase/auth';
import '../styles/Navbar.css'; 
// Importe o √≠cone que voc√™ usa, se houver (ex: um √≠cone de pessoa ou de menu)
// Vamos usar um emoji simples ou um texto para simular o √≠cone.

export default function Navbar() {
    const [currentUser, setCurrentUser] = useState(null);
    // isDropdownOpen controla o menu de usu√°rio (Ol√°, Max)
    const [isDropdownOpen, setIsDropdownOpen] = useState(false); 
    // isMobileMenuOpen controla o menu sandu√≠che (telas pequenas)
    const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false); 
    
    const navigate = useNavigate();
    const dropdownRef = useRef(null); // Refer√™ncia para fechar o dropdown de usu√°rio

    useEffect(() => {
        const unsubscribe = auth.onAuthStateChanged(user => {
            setCurrentUser(user);
        });

        // L√≥gica para fechar o dropdown ao clicar fora
        const handleClickOutside = (event) => {
            if (dropdownRef.current && !dropdownRef.current.contains(event.target)) {
                setIsDropdownOpen(false);
            }
        };

        document.addEventListener('mousedown', handleClickOutside);
        return () => {
            unsubscribe();
            document.removeEventListener('mousedown', handleClickOutside);
        };
    }, []);

    const handleLogout = async () => {
        try {
            await signOut(auth);
            setIsDropdownOpen(false); // Fecha o dropdown
            setIsMobileMenuOpen(false); // Fecha o menu mobile
            navigate('/');
        } catch (error) {
            console.error("Erro ao fazer logout:", error);
        }
    };
    
    // Fun√ß√£o para alternar o estado do dropdown de usu√°rio
    const toggleDropdown = () => {
        setIsDropdownOpen(prev => !prev);
    };

    // NOVO: Fun√ß√£o para alternar o estado do menu sandu√≠che
    const toggleMobileMenu = () => {
        setIsMobileMenuOpen(prev => !prev);
    };


    return (
        <>
            <nav className="navbar">
                <div className="navbar-left">
                    <button 
                        className="menu-button" 
                        onClick={toggleMobileMenu} // VINCULADO AO NOVO TOGGLE
                    >
                        ‚ò∞ {/* √çcone de Sandu√≠che */}
                    </button>
                    <Link to="/" className="logo">
                        Checkup Azul
                    </Link>
                </div>
                
                {/* O restante do seu c√≥digo (navbar-right) */}
                <div className="navbar-right">
                    
                    {currentUser ? (
                        // --- MENU DROP-DOWN PARA USU√ÅRIO LOGADO ---
                        <div className="dropdown-menu-container" ref={dropdownRef}>
                            <button onClick={toggleDropdown} className="dropdown-toggle-button">
                                Ol√°, {currentUser.email.split('@')[0]} <span>&#9662;</span> 
                            </button>
                            
                            {isDropdownOpen && (
                                <div className="dropdown-content">
                                    <Link 
                                        to="/lembretes" 
                                        className="dropdown-item" 
                                        onClick={() => setIsDropdownOpen(false)}
                                    >
                                        üìÖ Meus Lembretes
                                    </Link>
                                    <button 
                                        onClick={handleLogout} 
                                        className="dropdown-item logout-button"
                                    >
                                        Sair
                                    </button>
                                </div>
                            )}
                        </div>
                    ) : (
                        // --- LINKS PARA USU√ÅRIO N√ÉO LOGADO ---
                        <>
                            <Link to="/login" className="nav-button">Login</Link>
                            <Link to="/cadastro" className="nav-button signup-btn">Cadastrar</Link>
                        </>
                    )}
                </div>
            </nav>

            {/* === 2. ESTRUTURA DO MENU MOBILE FORA DO NAV === */}
            {/* Usa a classe 'open' para mostrar/esconder, controlada pelo isMobileMenuOpen */}
            <div className={`mobile-menu ${isMobileMenuOpen ? 'open' : ''}`}>
                <Link to="/" className="mobile-menu-item" onClick={toggleMobileMenu}>
                    üè† Home
                </Link>
                {currentUser && (
                    <Link to="/lembretes" className="mobile-menu-item" onClick={toggleMobileMenu}>
                        üìÖ Meus Lembretes
                    </Link>
                )}
                
                {/* Op√ß√µes para usu√°rio n√£o logado */}
                {!currentUser && (
                    <>
                        <Link to="/login" className="mobile-menu-item" onClick={toggleMobileMenu}>
                            Login
                        </Link>
                        <Link to="/cadastro" className="mobile-menu-item" onClick={toggleMobileMenu}>
                            Cadastrar
                        </Link>
                    </>
                )}

                {/* Bot√£o de Logout para usu√°rio logado */}
                {currentUser && (
                    <button 
                        onClick={handleLogout} 
                        className="mobile-menu-item logout-button-mobile"
                    >
                        Sair
                    </button>
                )}
            </div>
        </>
    );
}
