import React, { useEffect, useState } from "react";
import { useParams, Link } from "react-router-dom";
import "../styles/Quiz.css";

export default function QuizResultado() {
  const { userId } = useParams();
  const [resultado, setResultado] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchResultado = async () => {
      try {
        const res = await fetch(`http://localhost:3000/quizresultado/${userId}`);
        if (!res.ok) throw new Error("Erro ao buscar resultado");
        const data = await res.json();
        setResultado(data);
      } catch (err) {
        console.error("Erro ao carregar resultado:", err);
      } finally {
        setLoading(false);
      }
    };
    fetchResultado();
  }, [userId]);

  if (loading) {
    return <p className="quiz-loading">Carregando resultado...</p>;
  }

  if (!resultado) {
    return (
      <div className="quiz-resultado">
        <h2>Nenhum resultado encontrado</h2>
        <Link to="/" className="quiz-botao">
          Voltar para a Home
        </Link>
      </div>
    );
  }

  return (
    <div className="quiz-container">
      <div className="quiz-resultado">
        <h2>{resultado.result_title}</h2>
        <p>{resultado.result_text}</p>
        <p className="quiz-score">Pontuação total: {resultado.score}</p>
        <Link to="/" className="quiz-botao">
          Voltar para a Home
        </Link>
      </div>
    </div>
  );
}
