import React, { useEffect, useState } from "react";
import { useParams, Link } from "react-router-dom";
import "../styles/Quiz.css";

export default function QuizResultado() {
  const { userId } = useParams();
  const [resultado, setResultado] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchResultado = async () => {
      try {
        const res = await fetch(`http://localhost:3000/quizresultado/${userId}`);
        if (!res.ok) throw new Error("Erro ao buscar resultado");
        const data = await res.json();
        setResultado(data);
      } catch (err) {
        console.error("Erro ao carregar resultado:", err);
      } finally {
        setLoading(false);
      }
    };
    fetchResultado();
  }, [userId]);

  if (loading) {
    return <p className="quiz-loading">Carregando resultado...</p>;
  }

  if (!resultado) {
    return (
      <div className="quiz-resultado">
        <h2>Nenhum resultado encontrado</h2>
        <Link to="/" className="quiz-botao">
          Voltar para a Home
        </Link>
      </div>
    );
  }

return (
    <div className="quiz-container">
      {!finalResult ? (
        <div className="quiz-pergunta">
          <h2>{perguntas[etapa].pergunta}</h2>
          <div className="quiz-opcoes">
            {perguntas[etapa].opcoes.map((opcao, index) => (
              <button
                key={index}
                onClick={() => handleResposta(opcao.valor)}
                className="quiz-botao"
              >
                {opcao.texto}
              </button>
            ))}
          </div>
          <p className="quiz-progresso">
            Pergunta {etapa + 1} de {perguntas.length}
          </p>
        </div>
      ) : (
        <div className="quiz-resultado">
          <h2>{finalResult.titulo}</h2>
          <p>{finalResult.texto}</p>
          <p className="quiz-score">Pontuação total: {pontuacaoTotal}</p>
        </div>
      )}
    </div>
  );
}
