import React, { useState } from "react";

const perguntas = [
  {
    pergunta: "Com que frequência você pratica exercícios físicos?",
    opcoes: [
      "Nunca",
      "1-2 vezes por semana",
      "3-5 vezes por semana",
      "Todos os dias",
    ],
  },
  {
    pergunta: "Como você avalia sua alimentação diária?",
    opcoes: [
      "Muito ruim",
      "Ruim",
      "Regular",
      "Boa",
      "Muito boa",
    ],
  },
  {
    pergunta: "Com que frequência você faz check-ups médicos?",
    opcoes: [
      "Nunca",
      "Raramente",
      "Às vezes",
      "Regularmente",
    ],
  },
];

export default function Quiz() {
  const [perguntaAtual, setPerguntaAtual] = useState(0);
  const [respostas, setRespostas] = useState([]);

  const handleResposta = (opcao) => {
    setRespostas([...respostas, opcao]);
    if (perguntaAtual < perguntas.length - 1) {
      setPerguntaAtual(perguntaAtual + 1);
    } else {
      // Quiz finalizado
      alert("Quiz finalizado! Suas respostas: " + respostas.join(", "));
      // Aqui você pode chamar a API para salvar os resultados
    }
  };

  return (
    <div className="quiz-container">
      <h2>Quiz de Saúde Masculina</h2>
      <div className="pergunta-section">
        <p>{perguntas[perguntaAtual].pergunta}</p>
        <div className="opcoes-section">
          {perguntas[perguntaAtual].opcoes.map((opcao, index) => (
            <button key={index} onClick={() => handleResposta(opcao)}>
              {opcao}
            </button>
          ))}
        </div>
      </div>
    </div>
  );
}
