import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import GraficoMortalidade from "../components/GraficoMortalidade";
import "../styles/Home.css";

export default function Home() {
  const [user, setUser] = useState(null);
  const [quizCompleted, setQuizCompleted] = useState(false);
  const [loadingQuizStatus, setLoadingQuizStatus] = useState(true);
  const navigate = useNavigate();

  useEffect(() => {
    // Pega usuário e token do localStorage
    const storedUser = JSON.parse(localStorage.getItem("usuario"));
    const token = localStorage.getItem("token");

    if (!storedUser || !token) {
      setUser(null);
      setLoadingQuizStatus(false);
      return;
    }

    setUser(storedUser);

    // Verifica se já fez o quiz
    const checkQuizStatus = async () => {
      try {
        const response = await fetch(`http://localhost:3000/quiz/${storedUser.id}`);
        if (!response.ok) {
          if (response.status === 404) {
            setQuizCompleted(false);
          } else {
            throw new Error("Erro ao buscar resultado do quiz");
          }
        } else {
          setQuizCompleted(true);
        }
      } catch (error) {
        console.error(error);
        setQuizCompleted(false);
      } finally {
        setLoadingQuizStatus(false);
      }
    };

    checkQuizStatus();
  }, []);

  const handleQuizClick = () => {
    if (!user) return;
    if (quizCompleted) {
      // Vai para a página do resultado salvo
      navigate(`/quizresultado/${user.id}`);
    } else {
      // Inicia o quiz
      navigate("/quiz");
    }
  };

  return (
    <div className="home-container page-wrapper">
      <div className="home-hero">
        <div className="overlay"></div>
        <div className="home-content">
<h1>Bem-vindo ao Checkup Azul</h1>

<p>
  O <strong>Novembro Azul</strong> é um movimento dedicado à conscientização sobre a saúde masculina, incentivando a prevenção, o autocuidado e o acompanhamento médico regular.  
  Cuidar de si é um gesto de responsabilidade — e pode começar com passos simples no dia a dia.
</p>

<p>
  Pensando nisso, criamos o <strong>Quiz Checkup Azul</strong>.  
  Aqui, você responde a algumas perguntas sobre seus hábitos e rotina, e ao final recebe uma avaliação personalizada com sugestões práticas para melhorar seu bem-estar.
</p>

<p>
  Leva apenas alguns minutos e pode ser o primeiro passo para uma vida mais saudável. Vamos começar?
</p>


          {loadingQuizStatus ? (
            <button className="quiz-button-home" disabled>
              Carregando status...
            </button>
          ) : user ? (
            <button className="quiz-button-home" onClick={handleQuizClick}>
              {quizCompleted ? "Ver o seu resultado" : "Iniciar Quiz"}
            </button>
          ) : (
            <p className="login-text">
              Faça <strong>login ou cadastre-se</strong> para acessar o Quiz de Saúde Masculina.
            </p>
          )}
        </div>
      </div>

      <section className="grafico-section">
        <GraficoMortalidade />
      </section>
    </div>
  );
}
