import { useEffect, useState } from "react";
import { auth } from "../services/firebaseService";
import Quiz from "../components/Quiz";
// Removida importação duplicada de Navbar (a Navbar deve estar no App.js, mas vou mantê-la aqui por enquanto)
import GraficoMortalidade from "../components/GraficoMortalidade";
import "../styles/Home.css";

export default function Home() {
  const [user, setUser] = useState(null);
  // NOVO ESTADO: Controla se o componente Quiz deve ser exibido
  const [showQuiz, setShowQuiz] = useState(false); 

  useEffect(() => {
    const unsubscribe = auth.onAuthStateChanged(u => setUser(u));
    // Quando o usuário muda, reinicia o estado de showQuiz
    if (!u) {
        setShowQuiz(false); 
    }
    return () => unsubscribe();
  }, []);
  
  // Função para lidar com o clique do botão
  const handleStartQuiz = () => {
    setShowQuiz(true);
  };
  
  // Se o usuário estiver logado E showQuiz for verdadeiro, renderiza o Quiz
  if (user && showQuiz) {
    // Certifique-se de que o Quiz está envolto no wrapper de layout, 
    // se você tiver configurado um, para garantir o padding da Navbar.
    return (
        <div className="page-wrapper">
             <Quiz />
        </div>
    );
  }

  // Conteúdo da página Home (Hero Section e Gráfico)
  return (
    <div className="home-container page-wrapper"> {/* Adicionado page-wrapper para padding da Navbar */}

      {/* Seção Hero: Mostrada se o Quiz não estiver ativo */}
      <div className="home-hero">
        <div className="overlay"></div>
        <div className="home-content">
          <h1>Bem-vindo ao Checkup Azul</h1>
          <p>
            Cuidar da saúde é um ato de coragem e autocuidado. <br />
            Aqui no <strong>Checkup Azul</strong>, você encontra artigos sobre
            saúde masculina que descomplicam o que realmente importa — corpo,
            mente e bem-estar.
          </p>
          <p>
            Além disso, você pode participar do nosso <strong>Quiz interativo</strong>,
            que vai te ajudar a descobrir como está a sua rotina de cuidados e
            indicar dicas personalizadas para melhorar seus hábitos e alcançar
            uma vida mais equilibrada. <br />
            Comece hoje mesmo o seu Checkup Azul — pequenas mudanças fazem uma
            grande diferença!
          </p>

          {user ? (
            // Adicionado onClick para alternar o estado showQuiz
            <button className="quiz-button" onClick={handleStartQuiz}>
                Iniciar Quiz
            </button>
          ) : (
            <p className="login-text">
              Faça login ou cadastre-se para acessar o Quiz de Saúde Masculina.
            </p>
          )}
        </div>
      </div>
    
      {/* === Seção do Gráfico (mostrada abaixo da Home Hero) === */}
      <section className="grafico-section">
        <GraficoMortalidade />
      </section>
      
    </div>
  );
}
