import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";

export default function QuizResultado() {
  const { userId } = useParams();
  const [resultado, setResultado] = useState(null);

  useEffect(() => {
    const fetchResultado = async () => {
      try {
        const res = await fetch(`http://localhost:3000/quizresultado/${userId}`);
        if (!res.ok) throw new Error("Erro ao buscar resultado");
        const data = await res.json();
        setResultado(data);
      } catch (err) {
        console.error(err);
      }
    };
    fetchResultado();
  }, [userId]);

  if (!resultado) {
    return (
      <div
        style={{
          backgroundColor: "#000",
          color: "#fff",
          height: "100vh",
          display: "flex",
          justifyContent: "center",
          alignItems: "center",
        }}
      >
        <p>Carregando resultado...</p>
      </div>
    );
  }

  const { pontuacao_total } = resultado;

  let mensagem = "";
  if (pontuacao_total <= 5)
    mensagem = "É importante cuidar melhor da sua saúde. Busque orientação médica.";
  else if (pontuacao_total <= 10)
    mensagem = "Você está indo bem, mas há espaço para melhorar seus hábitos.";
  else mensagem = "Parabéns! Sua saúde está em dia, continue assim.";

  return (
    <div
      style={{
        backgroundColor: "#000",
        color: "#fff",
        minHeight: "100vh",
        display: "flex",
        justifyContent: "center",
        alignItems: "center",
      }}
    >
      <div
        style={{
          border: "1px solid #007BFF",
          padding: "40px",
          borderRadius: "10px",
          textAlign: "center",
          width: "50%",
        }}
      >
        <h2 style={{ color: "#fff", marginBottom: "10px" }}>Atenção!</h2>
        <p style={{ fontSize: "16px", marginBottom: "15px" }}>{mensagem}</p>
        <p>Pontuação total: {pontuacao_total}</p>
      </div>
    </div>
  );
}
