import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

export default function Quiz() {
  const navigate = useNavigate();

  const perguntas = [
    {
      pergunta: "Você se exercita regularmente?",
      opcoes: ["Sim", "Não"],
    },
    {
      pergunta: "Você faz checkup anual?",
      opcoes: ["Sim", "Não"],
    },
    // ... outras perguntas
  ];

  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [respostas, setRespostas] = useState([]);
  const [quizFinished, setQuizFinished] = useState(false);

  const handleResposta = (opcao) => {
    setRespostas([...respostas, opcao]);

    if (currentQuestion + 1 < perguntas.length) {
      setCurrentQuestion(currentQuestion + 1);
    } else {
      setQuizFinished(true);
      // Aqui você pode enviar o resultado pro backend, se já estiver pronto
      // ex: saveQuizResult(userId, respostas)
    }
  };

  const handleVoltar = () => {
    navigate("/"); // Volta pra Home
  };

  return (
    <div className="quiz-container">
      {!quizFinished ? (
        <div className="quiz-question">
          <h2>{perguntas[currentQuestion].pergunta}</h2>
          {perguntas[currentQuestion].opcoes.map((opcao, idx) => (
            <button
              key={idx}
              onClick={() => handleResposta(opcao)}
              className="quiz-option"
            >
              {opcao}
            </button>
          ))}
        </div>
      ) : (
        <div className="quiz-end">
          <h2>Parabéns! Você concluiu o quiz.</h2>
          <p>Suas respostas foram registradas.</p>
          <button onClick={handleVoltar} className="back-button">
            Voltar
          </button>
        </div>
      )}
    </div>
  );
}
