import React, { useState, useRef, useContext, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { UserContext } from '../context/UserContext';
import '../styles/Navbar.css';

export default function Navbar() {
  const { user, setUser } = useContext(UserContext);
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const navigate = useNavigate();
  const menuRef = useRef(null);

  // Fecha o menu ao clicar fora
  useEffect(() => {
    const handleClickOutside = (event) => {
      if (menuRef.current && !menuRef.current.contains(event.target)) {
        setIsMenuOpen(false);
      }
    };
    document.addEventListener('mousedown', handleClickOutside);
    return () => document.removeEventListener('mousedown', handleClickOutside);
  }, []);

const handleLogout = async () => {
  const token = localStorage.getItem("token");

  if (token) {
    try {
      await fetch("http://localhost:3000/logout", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ session_token: token }),
      });
    } catch (err) {
      console.error("Erro ao fazer logout:", err);
    }
  }

  // Limpa localStorage e contexto
  localStorage.removeItem("usuario");
  localStorage.removeItem("token");
  setUser(null);
  setIsMenuOpen(false);
  navigate("/");
};



  const toggleMenu = () => setIsMenuOpen(prev => !prev);
  const handleLinkClick = () => setIsMenuOpen(false);

  return (
    <nav className="navbar">
      <div className="navbar-left">
        <Link to="/" className="logo">Checkup Azul</Link>
      </div>

      <div className="navbar-right" ref={menuRef}>
        {user ? (
          <div className="dropdown-menu-container">
            <button onClick={toggleMenu} className="menu-button">‚ò∞</button>

            {isMenuOpen && (
              <div className="dropdown-content">
                <div className="dropdown-item user-info" style={{ fontWeight: 'bold' }}>
                  Ol√°, {user.email.split('@')[0]}
                </div>
                <hr style={{ margin: 0, borderColor: '#333' }} />

                <Link to="/" className="dropdown-item" onClick={handleLinkClick}>
                  üè† Home / Quiz
                </Link>
                <Link to="/lembretes" className="dropdown-item" onClick={handleLinkClick}>
                  üìÖ Meus Lembretes
                </Link>

                <button onClick={handleLogout} className="dropdown-item logout-button">
                  üö™ Sair
                </button>
              </div>
            )}
          </div>
        ) : (
          <>
            <Link to="/login" className="nav-button">Login</Link>
            <Link to="/cadastro" className="nav-button signup-btn">Cadastrar</Link>
          </>
        )}
      </div>
    </nav>
  );
}
