import { useEffect, useRef, useState } from "react";
import {
  BarChart,
  Bar,
  XAxis,
  YAxis,
  Tooltip,
  Legend,
  ResponsiveContainer,
  CartesianGrid,
} from "recharts";

export default function GraficoMortalidade() {
  const ref = useRef(null);
  
  // 1. Estado para controlar qual visualização está ativa
  const [visao, setVisao] = useState("total"); // Pode ser 'total' ou 'mensal'

  // Novo Total Geral (Feminino + Masculino)
  const TOTAL_GERAL = 815322; // 394.083 + 421.239

  // Dados para a visualização MENSAL (Ignorado removido)
  const dadosMensal = [
    { mes: "Jan", Feminino: 31166, Masculino: 33635 },
    { mes: "Fev", Feminino: 28526, Masculino: 31540 },
    { mes: "Mar", Feminino: 32799, Masculino: 36295 },
    { mes: "Abr", Feminino: 33055, Masculino: 36204 },
    { mes: "Mai", Feminino: 35158, Masculino: 37396 },
    { mes: "Jun", Feminino: 34076, Masculino: 36534 },
    { mes: "Jul", Feminino: 35152, Masculino: 38039 },
    { mes: "Ago", Feminino: 34812, Masculino: 36872 },
    { mes: "Set", Feminino: 32622, Masculino: 34412 },
    { mes: "Out", Feminino: 32074, Masculino: 34137 },
    { mes: "Nov", Feminino: 30461, Masculino: 32691 },
    { mes: "Dez", Feminino: 31382, Masculino: 33484 },
  ];

  // Dados para a visualização TOTAL (Ignorado removido)
  const dadosTotal = [
    {
      categoria: "Total Consolidado",
      Feminino: 394083,
      Masculino: 421239,
    },
  ];
  
  // Função para alternar o estado da visualização
  const toggleVisao = () => {
    setVisao(visao === "total" ? "mensal" : "total");
  };

  useEffect(() => {
    const observer = new IntersectionObserver(
      entries => {
        entries.forEach(entry => {
          if (entry.isIntersecting) {
            entry.target.classList.add("visible");
          }
        });
      },
      { threshold: 0.3 }
    );

    if (ref.current) observer.observe(ref.current);
  }, []);

  // Renderização do Gráfico com base no estado 'visao'
  const renderizarGrafico = () => {
    const data = visao === "total" ? dadosTotal : dadosMensal;
    const dataKeyX = visao === "total" ? "categoria" : "mes";

    return (
      <ResponsiveContainer width="100%" height={300}>
        <BarChart data={data} margin={{ top: 20, right: 30, left: 20, bottom: 5 }}>
          <CartesianGrid strokeDasharray="3 3" stroke="#ccc" />
          <XAxis dataKey={dataKeyX} tick={{ fill: "#fff", fontSize: 12 }} />
          <YAxis tick={{ fill: "#fff", fontSize: 12 }} />
          <Tooltip 
            formatter={(value) => [value.toLocaleString('pt-BR'), 'Casos']}
          />
          <Legend />
          {/* Apenas duas barras */}
          <Bar dataKey="Feminino" fill="#ff7f0e" name="Feminino" />
          <Bar dataKey="Masculino" fill="#1f77b4" name="Masculino" />
        </BarChart>
      </ResponsiveContainer>
    );
  };

  // Estilos inline para o texto de conscientização
  const estiloConscientizacao = {
    marginTop: '30px',
    padding: '20px',
    backgroundColor: '#3a3f47', // Um tom um pouco mais claro que o fundo
    borderRadius: '5px',
    textAlign: 'left',
    borderLeft: '5px solid #ff7f0e', // Linha de destaque laranja
    color: '#ddd',
  };

  const estiloTituloConscientizacao = {
    color: '#ff7f0e',
    marginTop: 0,
    marginBottom: '10px',
    fontSize: '1.2em',
  };

  const estiloLista = {
    listStyleType: 'none',
    paddingLeft: 0,
    marginTop: '10px',
  };

  const estiloListItem = {
    marginBottom: '5px',
    fontWeight: 300,
  };

  return (
    <div ref={ref} className="grafico-container fade-in">
      {/* Título dinâmico */}
      <h2>
        {visao === "total"
          ? "Totais Consolidados de Casos por Gênero no Brasil"
          : "Distribuição Mensal de Casos por Gênero no Brasil"}
      </h2>
      {/* Subtítulo com o novo total */}
      <p>(Total Geral Feminino + Masculino: {TOTAL_GERAL.toLocaleString('pt-BR')})</p>

      {renderizarGrafico()}

      {/* Botão para alternar a visualização */}
      <button 
        onClick={toggleVisao}
        style={{
          marginTop: '20px',
          padding: '10px 15px',
          backgroundColor: '#00aaff',
          color: 'white',
          border: 'none',
          borderRadius: '5px',
          cursor: 'pointer',
          fontSize: '16px',
        }}
      >
        {visao === "total" ? "Ver Distribuição Mensal (Jan-Dez)" : "Ver Totais Consolidados"}
      </button>

      {/* NOVO TEXTO DE CONSCIENTIZAÇÃO */}
      <div style={estiloConscientizacao}>
        <h3 style={estiloTituloConscientizacao}>A sua saúde não é um jogo.</h3>
        <p>
          Os números de mortalidade entre a população masculina no Brasil acendem um alerta. 
          Enquanto as <strong style={{color: '#ff7f0e'}}>causas externas</strong> (como acidentes e violência) 
          dominam as estatísticas em homens jovens, o risco de morte prematura por doenças 
          que poderiam ser prevenidas é alarmante na vida adulta.
        </p>
        <p>
          As doenças do aparelho circulatório, câncer e problemas digestivos estão entre as 
          principais responsáveis por tirar a vida de milhares de homens todos os anos. 
          O cuidado e a prevenção são as suas maiores defesas.
        </p>
        <p style={{marginBottom: '5px'}}>
          <strong style={{color: '#fff'}}>Principais Doenças Crônicas que Atingem a Saúde Masculina:</strong>
        </p>
        <ul style={estiloLista}>
          <li style={estiloListItem}>✓ Doenças do Aparelho Circulatório (Infarto, AVC, Hipertensão)</li>
          <li style={estiloListItem}>✓ Neoplasias (Câncer, com destaque para pulmão e próstata)</li>
          <li style={estiloListItem}>✓ Doenças do Aparelho Digestivo (como doenças hepáticas relacionadas ao álcool)</li>
          <li style={estiloListItem}>✓ Doenças do Aparelho Respiratório</li>
          <li style={estiloListItem}>✓ Diabetes e Doenças Endócrinas</li>
        </ul>
        <p>
          Não espere o pior. Fazer check-ups regulares não é sinal de fraqueza, 
          é um ato de <strong style={{color: '#61dafb'}}>responsabilidade</strong> com o seu futuro.
        </p>
      </div>
    </div>
  );
}
