// src/components/Desafios.js
import { useEffect, useState, useContext } from "react";
import { db } from "../services/firebaseService";
import { collection, getDocs, doc, updateDoc } from "firebase/firestore";
import { UserContext } from "../context/UserContext";

export default function Desafios() {
  const [desafios, setDesafios] = useState([]);
  const { user } = useContext(UserContext);

  useEffect(() => {
    const fetchDesafios = async () => {
      const querySnapshot = await getDocs(collection(db, "desafios"));
      setDesafios(querySnapshot.docs.map(doc => ({ id: doc.id, ...doc.data() })));
    };
    fetchDesafios();
  }, []);

  const marcarConcluido = async (id) => {
    const desafioRef = doc(db, "desafios", id);
    await updateDoc(desafioRef, { concluido: true, userId: user.uid });
    setDesafios(prev => prev.map(d => d.id === id ? { ...d, concluido: true } : d));
  };

  return (
    <div>
      <h2>Desafios de Saúde</h2>
      {desafios.map(d => (
        <div key={d.id}>
          <p>{d.descricao}</p>
          <button disabled={d.concluido} onClick={() => marcarConcluido(d.id)}>
            {d.concluido ? "Concluído ✅" : "Marcar como Concluído"}
          </button>
        </div>
      ))}
    </div>
  );
}
