import { useEffect, useRef } from "react";
import {
  BarChart,
  Bar,
  XAxis,
  YAxis,
  Tooltip,
  Legend,
  ResponsiveContainer,
  CartesianGrid,
} from "recharts";
import "./GraficoMortalidade.css"; // se quiser estilos separados

export default function GraficoMortalidade() {
  const ref = useRef(null);

  const dados = [
    { causa: "Doenças cardíacas", Brasil: 29, Mundo: 27 },
    { causa: "Câncer de pulmão", Brasil: 15, Mundo: 17 },
    { causa: "Acidentes de trânsito", Brasil: 11, Mundo: 8 },
    { causa: "Diabetes", Brasil: 7, Mundo: 6 },
    { causa: "Suicídio", Brasil: 5, Mundo: 4 },
  ];

  useEffect(() => {
    const observer = new IntersectionObserver(
      entries => {
        entries.forEach(entry => {
          if (entry.isIntersecting) {
            entry.target.classList.add("visible");
          }
        });
      },
      { threshold: 0.3 }
    );

    if (ref.current) observer.observe(ref.current);
  }, []);

  return (
    <div ref={ref} className="grafico-container fade-in">
      <h2>Principais causas de morte masculina em 2024</h2>
      <p>(Comparativo Brasil × Mundo)</p>

      <ResponsiveContainer width="100%" height={300}>
        <BarChart data={dados}>
          <CartesianGrid strokeDasharray="3 3" stroke="#333" />
          <XAxis dataKey="causa" tick={{ fill: "#fff", fontSize: 12 }} />
          <YAxis tick={{ fill: "#fff", fontSize: 12 }} />
          <Tooltip />
          <Legend />
          <Bar dataKey="Brasil" fill="#00aaff" />
          <Bar dataKey="Mundo" fill="#ffaa00" />
        </BarChart>
      </ResponsiveContainer>
    </div>
  );
}
