import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";

export default function Quiz({ user }) {
  const navigate = useNavigate();
  const [quizFinished, setQuizFinished] = useState(false);
  const [quizData, setQuizData] = useState([]); // suas perguntas
  const [answers, setAnswers] = useState({});

  useEffect(() => {
    // Aqui você carregaria suas perguntas
    setQuizData([
      { id: 1, question: "Pergunta 1?", options: ["A", "B", "C"] },
      { id: 2, question: "Pergunta 2?", options: ["A", "B", "C"] },
    ]);
  }, []);

  const handleAnswer = (questionId, option) => {
    setAnswers({ ...answers, [questionId]: option });
  };

  const handleSubmit = async () => {
    try {
      // Salva o resultado no backend
      const response = await fetch("http://localhost:3000/quiz", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          user_id: user.id,
          resultado: answers
        }),
      });

      if (!response.ok) throw new Error("Erro ao salvar resultado");

      setQuizFinished(true); // marca quiz como finalizado
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <div className="quiz-container">
      {!quizFinished ? (
        <>
          {quizData.map(q => (
            <div key={q.id}>
              <p>{q.question}</p>
              {q.options.map(o => (
                <button
                  key={o}
                  onClick={() => handleAnswer(q.id, o)}
                  className={answers[q.id] === o ? "selected" : ""}
                >
                  {o}
                </button>
              ))}
            </div>
          ))}
          <button onClick={handleSubmit} className="submit-quiz-button">
            Finalizar Quiz
          </button>
        </>
      ) : (
        <>
          <p>Quiz concluído!</p>
          <button onClick={() => navigate("/")} className="back-button">
            Voltar
          </button>
        </>
      )}
    </div>
  );
}
