// src/components/Quiz.js
import { useEffect, useState, useContext } from "react";
import { db } from "../services/firebaseService";
import { collection, getDocs } from "firebase/firestore";
import { UserContext } from "../context/UserContext";

export default function Quiz() {
  const [perguntas, setPerguntas] = useState([]);
  const [respostas, setRespostas] = useState({});
  const [pontuacao, setPontuacao] = useState(null);
  const { user } = useContext(UserContext);

  useEffect(() => {
    const fetchPerguntas = async () => {
      const querySnapshot = await getDocs(collection(db, "quiz"));
      setPerguntas(querySnapshot.docs.map(doc => ({ id: doc.id, ...doc.data() })));
    };
    fetchPerguntas();
  }, []);

  const handleResponder = (perguntaId, opcao) => {
    setRespostas(prev => ({ ...prev, [perguntaId]: opcao }));
  };

  const calcularPontuacao = () => {
    let pontos = 0;
    perguntas.forEach(p => {
      if (respostas[p.id] === p.correta) pontos++;
    });
    setPontuacao(pontos);
  };

  return (
    <div>
      <h2>Quiz Educativo</h2>
      {perguntas.map((p, i) => (
        <div key={p.id}>
          <p>{p.pergunta}</p>
          {p.opcoes.map((op, idx) => (
            <button key={idx} onClick={() => handleResponder(p.id, op)}>
              {op}
            </button>
          ))}
        </div>
      ))}
      <button onClick={calcularPontuacao}>Finalizar Quiz</button>
      {pontuacao !== null && <p>VocÃª marcou {pontuacao} ponto(s)!</p>}
    </div>
  );
}
