import { Link } from "react-router-dom";
import { auth, db } from "../services/firebaseService"; 
import { signOut } from "firebase/auth";
import { doc, getDoc } from "firebase/firestore";
import { useState, useEffect, useRef } from "react";
import "../styles/Navbar.css";

// Use useRef para rastrear o scrollY anterior de forma persistente
const HIDE_THRESHOLD = 80;

export default function Navbar() {
  const [user, setUser] = useState(null);
  const [userName, setUserName] = useState(null); 
  // NOVO ESTADO: Inicialmente true para evitar renderizar o email
  const [isLoadingName, setIsLoadingName] = useState(true); 
  const [isVisible, setIsVisible] = useState(true);
  const lastScrollY = useRef(0);

  // L√≥gica de Scroll (mantida do passo anterior)
  const handleScroll = () => {
    const currentScrollY = window.scrollY;

    if (currentScrollY < HIDE_THRESHOLD) { 
      setIsVisible(true);
    } 
    else if (currentScrollY > lastScrollY.current) {
      setIsVisible(false);
    } 
    else if (currentScrollY < lastScrollY.current) {
      setIsVisible(true);
    }
    
    lastScrollY.current = currentScrollY;
  };

  // 1. useEffect Principal: Autentica√ß√£o e Scroll
  useEffect(() => {
    const unsubscribeAuth = auth.onAuthStateChanged(u => setUser(u));
    window.addEventListener('scroll', handleScroll);

    return () => {
      unsubscribeAuth();
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  // 2. Novo useEffect: Buscar o nome no Firestore quando o usu√°rio logar
  useEffect(() => {
    // Resetar o estado ao iniciar a busca
    setIsLoadingName(true); 
    setUserName(null); 
    
    if (user) {
      const fetchUserName = async () => {
        try {
          const userDocRef = doc(db, "usuarios", user.uid);
          const userDoc = await getDoc(userDocRef);
          
          let finalName = user.email.split('@')[0]; // Fallback: Parte do email
          
          if (userDoc.exists() && userDoc.data().nome) {
            finalName = userDoc.data().nome.split(' ')[0]; // Usa a primeira palavra do nome
          }
          
          setUserName(finalName);
        } catch (error) {
          console.error("Erro ao buscar nome do usu√°rio:", error);
          // O fallback para o email j√° est√° em finalName
        } finally {
          // IMPORTANTE: O carregamento √© definido como falso AP√ìS a busca, 
          // garantindo que o userName j√° esteja preenchido (nome ou email).
          setIsLoadingName(false); 
        }
      };
      
      fetchUserName();
      
    } else {
      // Se n√£o h√° usu√°rio, n√£o h√° carregamento
      setIsLoadingName(false);
      setUserName(null); 
    }
  }, [user]); // Roda sempre que o estado 'user' mudar

  const handleLogout = async () => {
    await signOut(auth);
  };

  const navbarClasses = `navbar ${isVisible ? 'navbar-visible' : 'navbar-hidden'}`;

  // 3. Renderiza√ß√£o: Usar o userName e isLoadingName
  return (
    <nav className={navbarClasses}>
      <div className="navbar-left">
        <button className="menu-button">‚ò∞</button>
        <span className="logo">Checkup Azul</span>
      </div>

      <div className="navbar-center">
        <input
          type="text"
          className="search-bar"
          placeholder="Pesquisar"
        />
        <button className="search-icon">üîç</button>
      </div>

      <div className="navbar-right">
        {!user ? (
          <>
            <Link to="/login" className="nav-button login-btn">Login</Link>
            <Link to="/cadastro" className="nav-button signup-btn">Sign Up</Link>
          </>
        ) : (
          <>
            {/* Exibe 'Ol√°, Carregando...' durante a busca, ou 'Ol√°, [Nome/Email]' ap√≥s a busca. */}
            <span className="user-text">
                Ol√°, {isLoadingName ? 'Carregando...' : userName || 'Usu√°rio'}
            </span> 
            
            <button onClick={handleLogout} className="nav-button logout-btn">
              Logout
            </button>
          </>
        )}
      </div>
    </nav>
  );
}
