import { useState } from "react";
import { auth, db } from "../services/firebaseService";
import { createUserWithEmailAndPassword } from "firebase/auth";
import { doc, setDoc } from "firebase/firestore";
import { useNavigate, Link } from "react-router-dom";
import "../styles/Cadastro.css";


export default function Cadastro() {
  const [email, setEmail] = useState("");
  const [senha, setSenha] = useState("");
  const [nome, setNome] = useState("");
  const navigate = useNavigate();

  const handleCadastro = async (e) => {
    e.preventDefault();
    try {
      const userCredential = await createUserWithEmailAndPassword(auth, email, senha);
      await setDoc(doc(db, "usuarios", userCredential.user.uid), { nome, email });
      navigate("/"); // Redireciona para Home, que mostra o Quiz
    } catch (error) {
      alert(error.message);
    }
  };

  return (
    // Usa a classe para centralizar o formulário no espaço restante da tela
    <div className="auth-page-centered"> 
        {/* Seu container de cadastro, que tem a classe cadastro-container */}
        <div className="cadastro-container"> 
            <h2 className="cadastro-title">CheckupAzul</h2>
            <form onSubmit={handleCadastro} className="cadastro-form">
                {/* ... inputs e botão ... */}
            </form>
            <p className="cadastro-links">
                Já tem uma conta? <Link to="/login">Login</Link>
            </p>
        </div>
    </div>
  );
}
