import { Link } from "react-router-dom";
// Importar 'db' do firebaseService (assumindo que voc√™ o exportou)
import { auth, db } from "../services/firebaseService"; 
import { signOut } from "firebase/auth";
import { doc, getDoc } from "firebase/firestore"; // Importar fun√ß√µes do Firestore
import { useState, useEffect, useRef } from "react";
import "../styles/Navbar.css";

// Use useRef para rastrear o scrollY anterior de forma persistente
const HIDE_THRESHOLD = 80;

export default function Navbar() {
  const [user, setUser] = useState(null);
  // Novo estado para guardar o nome do usu√°rio
  const [userName, setUserName] = useState(null); 
  const [isVisible, setIsVisible] = useState(true);
  const lastScrollY = useRef(0);

  // L√≥gica de Scroll (mantida do passo anterior)
  const handleScroll = () => {
    const currentScrollY = window.scrollY;

    if (currentScrollY < HIDE_THRESHOLD) { 
      setIsVisible(true);
    } 
    else if (currentScrollY > lastScrollY.current) {
      setIsVisible(false);
    } 
    else if (currentScrollY < lastScrollY.current) {
      setIsVisible(true);
    }
    
    lastScrollY.current = currentScrollY;
  };

  // 1. useEffect Principal: Autentica√ß√£o e Scroll
  useEffect(() => {
    const unsubscribeAuth = auth.onAuthStateChanged(u => setUser(u));
    window.addEventListener('scroll', handleScroll);

    return () => {
      unsubscribeAuth();
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  // 2. Novo useEffect: Buscar o nome no Firestore quando o usu√°rio logar
  useEffect(() => {
    // S√≥ prossegue se houver um usu√°rio logado
    if (user) {
      const fetchUserName = async () => {
        try {
          // Obt√©m o documento do usu√°rio pelo UID
          const userDocRef = doc(db, "usuarios", user.uid);
          const userDoc = await getDoc(userDocRef);

          if (userDoc.exists()) {
            // Define o nome do usu√°rio, capitalizando a primeira letra (opcional)
            let fetchedName = userDoc.data().nome;
            // Se o nome n√£o for nulo, usa-o. Caso contr√°rio, usa o email.
            if (fetchedName) {
              // Pega apenas a primeira palavra se for o nome completo
              fetchedName = fetchedName.split(' ')[0]; 
            }
            setUserName(fetchedName);
          } else {
            // Se o documento n√£o existir, usa a parte do email antes do '@'
            setUserName(user.email.split('@')[0]);
          }
        } catch (error) {
          console.error("Erro ao buscar nome do usu√°rio:", error);
          // Em caso de erro, usa a parte do email
          setUserName(user.email.split('@')[0]);
        }
      };
      
      fetchUserName();
      
    } else {
      // Limpa o nome se o usu√°rio fizer logout
      setUserName(null); 
    }
  }, [user]); // Roda sempre que o estado 'user' mudar

  const handleLogout = async () => {
    await signOut(auth);
  };

  const navbarClasses = `navbar ${isVisible ? 'navbar-visible' : 'navbar-hidden'}`;

  // 3. Renderiza√ß√£o: Usar o userName
  return (
    <nav className={navbarClasses}>
      <div className="navbar-left">
        <button className="menu-button">‚ò∞</button>
        <span className="logo">Checkup Azul</span>
      </div>

      <div className="navbar-center">
        <input
          type="text"
          className="search-bar"
          placeholder="Pesquisar"
        />
        <button className="search-icon">üîç</button>
      </div>

      <div className="navbar-right">
        {!user ? (
          <>
            <Link to="/login" className="nav-button login-btn">Login</Link>
            <Link to="/cadastro" className="nav-button signup-btn">Sign Up</Link>
          </>
        ) : (
          <>
            {/* Exibe "Ol√°, [Nome]" ou "Ol√°, [Parte do Email]" */}
            <span className="user-text">Ol√°, {userName || user.email}</span> 
            <button onClick={handleLogout} className="nav-button logout-btn">
              Logout
            </button>
          </>
        )}
      </div>
    </nav>
  );
}
