import { useEffect, useState } from "react";

export default function QuizResultado() {
  const [resultado, setResultado] = useState(null);
  const [erro, setErro] = useState(null);

  useEffect(() => {
    // Pegando usuário logado do localStorage
    const storedUser = JSON.parse(localStorage.getItem("usuario"));

    if (!storedUser || !storedUser.id) {
      setErro("Nenhum usuário encontrado no sistema.");
      return;
    }

    const user_id = storedUser.id;

    async function carregarResultado() {
      try {
        const res = await fetch(`http://localhost:3000/quizresultado/${user_id}`);
        const dados = await res.json();

        if (!res.ok) {
          setErro(dados.erro || "Erro ao carregar resultado");
          return;
        }

        setResultado(dados);
      } catch (err) {
        console.error(err);
        setErro("Erro ao conectar ao servidor");
      }
    }

    carregarResultado();
  }, []);

  if (erro) {
    return (
      <div style={{ padding: 20, textAlign: "center", color: "red" }}>
        <h2>{erro}</h2>
      </div>
    );
  }

  if (!resultado) {
    return (
      <div style={{ padding: 20, textAlign: "center" }}>
        <h2>Carregando resultado...</h2>
      </div>
    );
  }

  return (
    <div style={{ padding: 20 }}>
      <h1>Resultado do Quiz</h1>

      <p><strong>Pontuação:</strong> {resultado.pontuacao}</p>
      <p><strong>Categoria:</strong> {resultado.categoria}</p>

      <p>
        <strong>Data:</strong>{" "}
        {new Date(resultado.created_at).toLocaleString("pt-BR")}
      </p>
    </div>
  );
}
