import React, { useState, useEffect, useRef } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import '../styles/Navbar.css'; 

export default function Navbar() {
    const [currentUser, setCurrentUser] = useState(null);
    const [isMenuOpen, setIsMenuOpen] = useState(false); 
    const navigate = useNavigate();
    const menuRef = useRef(null); 

    useEffect(() => {
        // Pega o usu치rio salvo no localStorage
        const user = JSON.parse(localStorage.getItem("usuario"));
        setCurrentUser(user || null);

        // L칩gica para fechar o menu ao clicar fora
        const handleClickOutside = (event) => {
            if (menuRef.current && !menuRef.current.contains(event.target)) {
                setIsMenuOpen(false);
            }
        };

        document.addEventListener('mousedown', handleClickOutside);
        return () => {
            document.removeEventListener('mousedown', handleClickOutside);
        };
    }, []);

    const handleLogout = () => {
        // Remove token e dados do usu치rio
        localStorage.removeItem("token");
        localStorage.removeItem("usuario");
        setCurrentUser(null);
        setIsMenuOpen(false);
        navigate('/');
    };
    
    const toggleMenu = () => {
        setIsMenuOpen(prev => !prev);
    };

    const handleLinkClick = () => {
        setIsMenuOpen(false);
    }

    return (
        <nav className="navbar">
            <div className="navbar-left">
                <Link to="/" className="logo">
                    Checkup Azul
                </Link>
            </div>
            
            <div className="navbar-right" ref={menuRef}> 
                {currentUser ? (
                    <div className="dropdown-menu-container">
                        <button onClick={toggleMenu} className="menu-button">
                            驕
                        </button>
                        
                        {isMenuOpen && (
                            <div className="dropdown-content">
                                <div className="dropdown-item user-info" style={{ fontWeight: 'bold' }}>
                                    Ol치, {currentUser.email.split('@')[0]}
                                </div>
                                <hr style={{margin: '0', borderColor: '#333'}} /> 

                                <Link to="/" className="dropdown-item" onClick={handleLinkClick}>
                                    游 Home / Quiz
                                </Link>

                                <Link to="/lembretes" className="dropdown-item" onClick={handleLinkClick}>
                                    游늰 Meus Lembretes
                                </Link>

                                <button onClick={handleLogout} className="dropdown-item logout-button">
                                    Sair
                                </button>
                            </div>
                        )}
                    </div>
                ) : (
                    <>
                        <Link to="/login" className="nav-button">Login</Link>
                        <Link to="/cadastro" className="nav-button signup-btn">Cadastrar</Link>
                    </>
                )}
            </div>
        </nav>
    );
}
