import { useState } from "react";
import { auth } from "../services/firebaseService";
import { signInWithEmailAndPassword } from "firebase/auth";
import { useNavigate, Link } from "react-router-dom";
import "../styles/Login.css";


export default function Login() {
  const [email, setEmail] = useState("");
  const [senha, setSenha] = useState("");
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      await signInWithEmailAndPassword(auth, email, senha);
      navigate("/"); // Redireciona para Home, que vai mostrar o Quiz
    } catch (error) {
      alert(error.message);
    }
  };

  return (
    // Usa a classe para centralizar o formulário no espaço restante da tela
    <div className="auth-page-centered"> 
        {/* Seu container de login, que tem a classe login-container */}
        <div className="login-container"> 
            <h2 className="login-title">CheckupAzul</h2>
            <form onSubmit={handleLogin} className="login-form">
                {/* ... inputs e botão ... */}
            </form>
            <p className="login-links">
                Ainda não tem uma conta? <Link to="/cadastro">Cadastrar-se</Link>
            </p>
        </div>
    </div>
  );
}
