import { Link } from "react-router-dom";
import { auth } from "../services/firebaseService";
import { signOut } from "firebase/auth";
import { useState, useEffect } from "react";
import "../styles/Navbar.css";

// Vari√°vel para rastrear a posi√ß√£o do scroll anterior. 
// Deve ser definida fora do componente ou dentro de useRef. 
// Usaremos uma vari√°vel fora para simplicidade, mas use useRef em projetos maiores.
let lastScrollY = 0; 
const HIDE_THRESHOLD = 80; // Ponto a partir do qual a navbar pode ser escondida

export default function Navbar() {
  const [user, setUser] = useState(null);
  // Estado para controlar se a navbar deve estar vis√≠vel
  const [isVisible, setIsVisible] = useState(true); 

  useEffect(() => {
    // L√≥gica de Autentica√ß√£o
    const unsubscribeAuth = auth.onAuthStateChanged(u => setUser(u));

    // L√≥gica de Scroll
    const handleScroll = () => {
      const currentScrollY = window.scrollY;

      // 1. Caso o usu√°rio esteja perto do topo, mostra a navbar
      if (currentScrollY < HIDE_THRESHOLD) { 
        setIsVisible(true);
      } 
      // 2. Se o usu√°rio rolou para baixo E j√° passou do ponto inicial (HIDE_THRESHOLD)
      else if (currentScrollY > lastScrollY && currentScrollY > HIDE_THRESHOLD) {
        setIsVisible(false); // Esconde a navbar
      } 
      // 3. Se o usu√°rio rolou para cima
      else if (currentScrollY < lastScrollY) {
        setIsVisible(true); // Mostra a navbar
      }
      
      lastScrollY = currentScrollY;
    };

    // Adiciona o listener de scroll
    window.addEventListener('scroll', handleScroll);

    // Fun√ß√£o de limpeza
    return () => {
      unsubscribeAuth();
      window.removeEventListener('scroll', handleScroll);
    };
  }, []); // Depend√™ncia vazia: roda apenas no mount e unmount

  const handleLogout = async () => {
    await signOut(auth);
  };

  // Define as classes da navbar dinamicamente
  const navbarClasses = `navbar ${isVisible ? 'navbar-visible' : 'navbar-hidden'}`;

  return (
    <nav className={navbarClasses}> {/* Usa a classe din√¢mica */}
      <div className="navbar-left">
        <button className="menu-button">‚ò∞</button>
        <span className="logo">Checkup Azul</span>
      </div>

      <div className="navbar-center">
        <input
          type="text"
          className="search-bar"
          placeholder="Pesquisar"
        />
        <button className="search-icon">üîç</button>
      </div>

      <div className="navbar-right">
        {!user ? (
          <>
            <Link to="/login" className="nav-button login-btn">Login</Link>
            <Link to="/cadastro" className="nav-button signup-btn">Sign Up</Link>
          </>
        ) : (
          <>
            <span className="user-text">Ol√°, {user.email}</span>
            <button onClick={handleLogout} className="nav-button logout-btn">
              Logout
            </button>
          </>
        )}
      </div>
    </nav>
  );
}
