import { useState } from "react";

const perguntas = [
  { id: 1, pergunta: "Você faz exames regulares?", opcoes: ["Sim", "Não"] },
  { id: 2, pergunta: "Você pratica atividades físicas?", opcoes: ["Sim", "Não"] },
  { id: 3, pergunta: "Você dorme bem?", opcoes: ["Sim", "Não"] },
  { id: 4, pergunta: "Você se alimenta de forma saudável?", opcoes: ["Sim", "Não"] },
  { id: 5, pergunta: "Você fuma?", opcoes: ["Sim", "Não"] },
  { id: 6, pergunta: "Você bebe álcool?", opcoes: ["Sim", "Não"] },
  { id: 7, pergunta: "Você faz check-up mental?", opcoes: ["Sim", "Não"] },
  { id: 8, pergunta: "Você faz pausas durante o trabalho?", opcoes: ["Sim", "Não"] },
  { id: 9, pergunta: "Você se hidrata corretamente?", opcoes: ["Sim", "Não"] },
  { id: 10, pergunta: "Você tem hábitos de lazer?", opcoes: ["Sim", "Não"] }
];

export default function Quiz() {
  const [respostas, setRespostas] = useState({});

  const handleChange = (id, valor) => {
    setRespostas(prev => ({ ...prev, [id]: valor }));
  };

  const handleSubmit = () => {
    alert("Respostas enviadas! " + JSON.stringify(respostas));
  };

  return (
    <div>
      {perguntas.map(q => (
        <div key={q.id}>
          <p>{q.pergunta}</p>
          {q.opcoes.map(o => (
            <label key={o}>
              <input
                type="radio"
                name={`pergunta-${q.id}`}
                value={o}
                onChange={() => handleChange(q.id, o)}
              />
              {o}
            </label>
          ))}
        </div>
      ))}
      <button onClick={handleSubmit}>Enviar</button>
    </div>
  );
}
